    private void txtBoyKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtBoyKeyReleased
        desiHesapla();
    }//GEN-LAST:event_txtBoyKeyReleased

    public void desiHesapla() {
        double yukseklik, en, boy;
        
        if(txtYukseklik.getText().isEmpty()) {yukseklik = 0;} else{ yukseklik = Double.valueOf(txtYukseklik.getText()); }
        if(txtEn.getText().isEmpty()) {en = 0;} else{ en = Double.valueOf(txtEn.getText()); }
        if(txtBoy.getText().isEmpty()) {boy = 0;} else{ boy = Double.valueOf(txtBoy.getText()); }
        
        double islem = yukseklik * en * boy;
        islem = islem / 3000;
        if(islem <= 0) {
            lblDesiResult.setText(String.valueOf(0));
            return; 
        }
        lblDesiResult.setText(String.valueOf(islem));
    }
    
